//The html is auto generated 
//by the Android client app.
//This is because Neocities is static.

//I need this to make global changes
//to all the prebuilt html files instead
//of rebuilding each file to reflect
//the changes.
function message() {
  window.onload = function() {
    document.getElementById("message").innerHTML="Please wait for the comments to fully load." ;
  }
}

 
message();
